package com.scd.exercise.y2022.m6.easy;

import sun.plugin2.message.transport.SerializingTransport;

import java.util.Arrays;
import java.util.HashSet;
import java.util.Set;

/**
 * @author James
 */
public class Code2309 {

    private static final int CHAR_UPPER_LOWER = 32;

    /**
     * 
     * @param s
     * @return
     */
    public String greatestLetter(String s) {
        int lens = s.length();
        Set<Character> charSet = new HashSet<>();
        for (int i = 0; i < lens; i++) {
            charSet.add(s.charAt(i));
        }
        char maxChar = 'A';
        boolean isFind = false;
        for (Character curChar : charSet) {
            if (Character.isLowerCase(curChar)) {
                char upperChar = (char) (curChar - CHAR_UPPER_LOWER);
                if (charSet.contains(upperChar)) {
                    if (upperChar >= maxChar) {
                        maxChar = upperChar;
                        isFind = true;
                    }
                }
            } else if (Character.isUpperCase(curChar)) {
                char lowerChar = (char) (curChar + CHAR_UPPER_LOWER);
                if (charSet.contains(lowerChar)) {
                    if (curChar >= maxChar) {
                        maxChar = curChar;
                        isFind = true;
                    }
                }
            }
        }
        if (isFind) {
            return String.valueOf(maxChar);
        }
        return "";
    }

    public static void main(String[] args) {
        char c = 'a';
        int num = 'a' - 'A';
        System.out.println(num);
        Code2309 code2309 = new Code2309();
        code2309.greatestLetter("");
    }
}
